int main()
{  
  DWORD fOldProtect = 0;
  if(!VirtualProtect(addData,sizeof(addData),PAGE_EXECUTE,&fOldProtect))
  {
    perror("VirtualProtect failed");
    system("pause");
  }
  FnAdd add = (FnAdd)addData;
  printf("%d",add(3,4));//7
  system("pause");
  return 0;
}
