    int test;
    A()
    {
        test = 0x666666;
        flagA = 0x111111;
    }
};

class B :virtual public A
{
public:
    int flagB;
    B()
    {
        flagB = 0X222222;
    }
};

class C :virtual public A
{
public:
    int flagC;
    C()
    {
        flagC = 0x3333333;
    }
};

class D :virtual public B,virtual public C
{
public:
    int flagD;
    D()
    {
        flagD = 0x444444;
    }
};
int main()
{
