{
	std::cout << this->name << std::endl;
}
char* A::get_name()
{
	return this->name;
}
A operator+(A &a1, A &a2)
{
	A a3("");
	char* buff = nullptr;
	int len = strlen(a1.name) + strlen(a2.name);
	if (buff = (char*)malloc(len + 1))
	{
		strcpy_s(buff, len + 1, a1.name);
		strcat_s(buff, len + 1, a2.name);
	}
	a3.name = buff;
	return a3;
}
ostream& operator<<(ostream& os, A &a1)
{
	os << a1.name;
	return os;
}

int main()
{
