            //可以在OD，xdbg中计算jmp的相对地址


		ret

	Func_GetModule:
	push ebp
		mov ebp, esp
		sub esp, 0x20
		push esi
		mov esi,dword ptr fs:[0x30]//PEB结构体地址
		mov esi,[esi+0xc]//LDR结构体地址
		mov esi,[esi+0x1c]//list
		mov esi,[esi]//list 第二项kernel32或者kernelbase
		mov esi,[esi+0x8]//dllbase
		mov eax,esi
		pop esi
		mov esp, ebp
		pop ebp
