		Func_GetAddr:
	    push ebp
		mov ebp, esp
		sub esp, 0x20
		mov edx, [ebp + 0x8]//dllbase
		mov esi, [edx + 0x3c]//if_anew
		lea esi, [esi + edx]//NT header
		mov esi, [esi + 0x78]//导出表RVA
		lea esi, [edx + esi]//导出表VA
		mov edi, [esi + 0x1c]//EAT RVA
		lea edi, [edi + edx]//EAT VA
