		lea ecx,[ebp-0x24]//printf字符串
		push ecx
		push eax//msvc.dll基址
		call [ebp-0xc]//调用GetProcAddress函数，两个参数，一个msvc.dll基址，一个字符串名称
		lea ecx,[ebp+0x2c]//hello world地址
		push ecx
		call eax//printf地址
		mov esp,ebp
		pop ebp
		ret


	}
}

