    int datalen = 26;
    // 假设这是加密或解密的密钥
    unsigned char key[] = "CynopsCTF{This_is_f4ke_f1ag}";
    int keylen = strlen((char*)key);
    // 对数据进行加密
    encrypt(input, datalen, key, keylen);
