    //上面可能是添加一个异常处理函数，然后下面让异常处理链表SEH增加一个节点，把这个异常处理函数插进去
    _asm
    {
        push myExceptHandler//fs: [0] 指向的是TIB[Thread information Block]结构中的EXCEPTION_REGISTRATION 结构
        mov eax,fs:[0]
        push eax
        mov fs:[0],esp//让fs:[0]指向一个新的EXCEPTION_REGISTRATION 结构(就像链表插入一个新节点)
    }
    //设置程序异常处
    int a = 1;
    a = a / 0;
    std::cout << flag;
    system("pause");
}
