)
{
	DbgPrintEx(77, 0, "DefCreate\r\n");
	IoCompleteRequest(Irp, 0);
	return STATUS_SUCCESS;
}

NTSTATUS DefDelete(
	_In_ struct _DEVICE_OBJECT* DeviceObject,
	_Inout_ struct _IRP* Irp
)
{
	DbgPrintEx(77, 0, "DefDelete\r\n");
	IoCompleteRequest(Irp, 0);
	return STATUS_SUCCESS;
}

NTSTATUS DefControl(
	_In_ struct _DEVICE_OBJECT* DeviceObject,
	_Inout_ struct _IRP* Irp
)
{
	DbgPrintEx(77, 0, "DefControl\r\n");
	IoCompleteRequest(Irp, 0);
	return STATUS_SUCCESS;
}

VOID DriverUnload(PDRIVER_OBJECT pDriver)
{
	if (pDriver->DeviceObject)
	{
		UNICODE_STRING UnSymeName = { 0 };
		RtlInitUnicodeString(&UnSymeName, _SYM_NAME);
		IoDeleteSymbolicLink(&UnSymeName);
		IoDeleteDevice(pDriver->DeviceObject);
	}
}

NTSTATUS DriverEntry(PDRIVER_OBJECT pDriver, PUNICODE_STRING pReg)
{
	pDriver->DriverUnload = DriverUnload;
	DbgPrint("hello driver");
	UNICODE_STRING UnDeviceName = { 0 };
	RtlInitUnicodeString(&UnDeviceName, _DEVICE_NAME);
	UNICODE_STRING UnSymeName = { 0 };
	RtlInitUnicodeString(&UnSymeName, _SYM_NAME);
	PDEVICE_OBJECT pDevice = NULL;
	//创建设备
	NTSTATUS st = IoCreateDevice(pDriver, 0, &UnDeviceName, FILE_DEVICE_UNKNOWN,FILE_DEVICE_SECURE_OPEN, FALSE, &pDevice);
	if(!NT_SUCCESS(st))
	{
		return STATUS_UNSUCCESSFUL;
	}
	//创建符号链接
	st = IoCreateSymbolicLink(&UnSymeName, &UnDeviceName);
	if (!NT_SUCCESS(st))
	{
		IoDeleteDevice(pDevice);
		return STATUS_UNSUCCESSFUL;
	}
	//设置设备标志,去除初始化表
	pDevice->Flags &= ~DO_DEVICE_INITIALIZING;
	//使用什么内存
	pDevice->Flags |= DO_BUFFERED_IO;

	pDriver->MajorFunction[IRP_MJ_CREATE] = DefCreate;
	pDriver->MajorFunction[IRP_MJ_CLOSE] = DefDelete;
	pDriver->MajorFunction[IRP_MJ_DEVICE_CONTROL] = DefControl;

	return STATUS_SUCCESS;
}
